function initTheme() {
    var moon = document.querySelector('.btn-moon');
    var sun = document.querySelector('.btn-sun');
    var table = document.querySelector('.table');
    var e = null !== localStorage.getItem("darkSwitch") && "dark" === localStorage.getItem("darkSwitch");
    if(table !== null){
        darkSwitch.checked = e, e ? (document.body.setAttribute("data-theme", "dark"), moon.classList.toggle("d-none"), sun.classList.toggle("d-none"), table.classList.add("table-dark")) : (document.body.removeAttribute("data-theme"),table.classList.remove("table-dark"))
    }else{
        darkSwitch.checked = e, e ? (document.body.setAttribute("data-theme", "dark"), moon.classList.toggle("d-none"), sun.classList.toggle("d-none")) : (document.body.removeAttribute("data-theme"))
    }
}

function resetTheme() {
    var table = document.querySelector('.table');
    if(table !== null){
        darkSwitch.checked ? (document.body.setAttribute("data-theme", "dark"), localStorage.setItem("darkSwitch", "dark"), table.classList.add("table-dark")) : (document.body.removeAttribute("data-theme"), localStorage.removeItem("darkSwitch"), table.classList.remove("table-dark"))
    }else{
        darkSwitch.checked ? (document.body.setAttribute("data-theme", "dark"), localStorage.setItem("darkSwitch", "dark")) : (document.body.removeAttribute("data-theme"), localStorage.removeItem("darkSwitch"))
    }
}
var darkSwitch = document.getElementById("darkSwitch");
window.addEventListener("load", function () {
    darkSwitch && (initTheme(), darkSwitch.addEventListener("change", function () {
        resetTheme()
    }))
});